import React from 'react';
import '../styles.css'
import messageService from '../services/messages'

class Home extends React.Component {

	constructor() {
		super()
		this.state = {
			messages: [],
			newMessage: '',
			user: null
		}
	}

	componentWillMount() {
		messageService
			.getAll()
			.then(messages => {
				this.setState({ messages })
			})

		const loggedUserJSON = window.localStorage.getItem('loggedUser')
		console.log(loggedUserJSON)
		if (loggedUserJSON) {
			const user = JSON.parse(loggedUserJSON)
			console.log(user)
			this.setState({ user })
			messageService.setToken(user.token)
		}
	}

	helloUser = () => {
		console.log('called')
		if (this.state.user !== null) {
			return <h3>Good day, {this.state.user.username} </h3>
		} else {
			return "Hello visitor"
		}
	}

	handleChange = (event) => {
		this.setState({ [event.target.name]: event.target.value })
	}

	addNewMessage = async (event) => {
		event.preventDefault()

		try {
			console.log('dsadsa')
			const user = await loginService.login({
				username: this.state.username,
				password: this.state.password
			})
			console.log('dsadsa')
			window.localStorage.setItem('loggedUser', JSON.stringify(user))
			console.log('onnistui')
			window.location.replace("http://localhost:3000");
		} catch (exception) {
			alert('Error! Failed login.')
		}
	}

	render() {

		return (
			<div class="login-dark">
				<form>
					<h1 class="sr-only">Insecure cyberbase app</h1>
					<div>{this.helloUser()}</div>
					<div class="illustration">
						<i class="icon ion-ios-locked-outline"></i>
					</div>
					<div class="form-group">
						<input value={this.state.newMessage} onChange={this.handleChange} class="form-control" name="message" placeholder="Add new message.." />
					</div>
				</form>
			</div>
		);
	}
}

export default Home;
