import React from 'react';
import '../styles.css'
import messageService from '../services/messages'

class Admin extends React.Component {

    constructor() {
        super()
        this.state = {
            messages: []
        }
    }

    componentWillMount() {
        messageService
            .getAll()
            .then(messages => {
                this.setState({ messages })
            })
    }

    viewMessages = () => {
        let messagesToRender = [];
        let messages = this.state.messages
        for (let i = 0; i < messages.length; i++) {
            messagesToRender.push(<li>{messages[i].content} ({messages[i].user.username})
            <button type="submit" onClick={() => this.deleteMessage(messages[i].id)}>Delete</button></li>)
        }
        return messagesToRender
    }

    deleteAllMessagesButton = () => {
        if (this.state.messages.length > 0) {
            return <button class="btn btn-primary btn-block" type="submit" onClick={this.deleteMessage(this.state.messages[0])}>Delte all messages</button>
        }
        return ""
    }

    deleteMessage = (id) => {
        messageService.remove(id)
    }

    render() {
        return (
            <div class="login-dark">
                <form>
                    <h1 class="sr-only">Insecure cyberbase app</h1>
                    <h2 class="sr-only">Admin tools</h2>
                    {this.deleteAllMessagesButton()}
                    <div class="illustration">
                    <i class="icon ion-ios-locked-outline"></i>
                </div>
                <ul>
                    {this.viewMessages()}
                </ul>
                <a href='/'>Back to main page</a>
                </form>
            </div >
        );
    }
}

export default Admin;
